<>
    <Box
        sx={{
            width: "100%",
            padding: "40px 0",
            display: "flex",
            justifyContent: "center",
            backgroundColor: "#f5f5f5",
        }}
    >
        <Paper
            elevation={1}
            sx={{
                padding: "20px",
                width: "100%",
                maxWidth: "1400px",
                display: "flex",
                gap: "20px",
                overflow: "hidden",
                borderRadius: "8px",
                boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
                margin: "20px",
                backgroundColor: "#ffffff",
            }}
        >
            {/* Sidebar Section */}
            <Box
                sx={{
                    width: { xs: '100%', sm: '25%' },
                    padding: '20px',
                    borderRight: '1px solid #ddd',
                    backgroundColor: '#f5f5f5',
                    borderRadius: '8px',
                    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '20px',
                    height: '100vh',
                    overflowY: 'auto',
                }}
            >
                <Typography variant="h6" gutterBottom>
                  Data Flow
                </Typography>
                <Divider sx={{ marginBottom: '10px' }} />
                <Link to="/home" style={{ textDecoration: 'none' }}>
                    <Box
                        sx={{
                            display: 'flex',
                            alignItems: 'center',
                            padding: '10px',
                            borderRadius: '4px',
                            transition: 'background-color 0.3s',
                            '&:hover': {
                                backgroundColor: '#e0e0e0',
                            },
                        }}
                    >
                        <HomeIcon sx={{ marginRight: '10px', color: 'black' }} />
                        <Typography variant="body1" sx={{ color: 'black' }}>
                            Home
                        </Typography>
                    </Box>
                </Link>
                <Link to="/about" style={{ textDecoration: 'none' }}>
                    <Box
                        sx={{
                            display: 'flex',
                            alignItems: 'center',
                            padding: '10px',
                            borderRadius: '4px',
                            transition: 'background-color 0.3s',
                            '&:hover': {
                                backgroundColor: '#e0e0e0',
                            },
                        }}
                    >
                        <InfoIcon sx={{ marginRight: '10px', color: 'black' }} />
                        <Typography variant="body1" sx={{ color: 'black' }}>
                            About
                        </Typography>
                    </Box>
                </Link>
                <Link to="/contact" style={{ textDecoration: 'none' }}>
                    <Box
                        sx={{
                            display: 'flex',
                            alignItems: 'center',
                            padding: '10px',
                            borderRadius: '4px',
                            transition: 'background-color 0.3s',
                            '&:hover': {
                                backgroundColor: '#e0e0e0',
                            },
                        }}
                    >
                        <ContactMailIcon sx={{ marginRight: '10px', color: 'black' }} />
                        <Typography variant="body1" sx={{ color: 'black' }}>
                            Contact
                        </Typography>
                    </Box>
                </Link>
            </Box>

            {/* Main Form Section */}
            <Box sx={{ width: "75%" }}>
                <Typography variant="h5" gutterBottom>
                    Audit Form
                </Typography>

                <form onSubmit={handleSubmit}>
                    <Grid container spacing={4}>
                        <Grid item xs={12} md={3}>
                            <CustomInputLabel label="Type of Audit" />
                            <CustomFormControl>
                                <CustomSelect
                                    value={audit_type}
                                    onChange={handleAuditChange}
                                    options={audit_type_check}
                                />
                            </CustomFormControl>
                        </Grid>
                        <Grid item xs={12} md={3}>
                            <CustomInputLabel label="Barcode" />
                            <CustomFormControl>
                                <CustomTextField
                                    value={barcode}
                                    onChange={handleInpChange}
                                    error={Boolean(error)}
                                    helperText={error}
                                    placeholder="Enter your text here"
                                    inputProps={{ maxLength: 16 }}
                                />
                            </CustomFormControl>
                        </Grid>
                        <Grid item xs={12} md={3}>
                            <CustomInputLabel label="Status" />
                            <CustomFormControl>
                                <CustomSelect
                                    value={case_status}
                                    onChange={handleCaseStatusChange}
                                    options={audit_type_status}
                                />
                            </CustomFormControl>
                        </Grid>
                        <Grid item xs={12} md={3}>
                            <CustomInputLabel label="Is there any case error?" />
                            <CustomFormControl>
                                <CustomSelect
                                    value={case_error}
                                    onChange={handleCaseErrorChange}
                                    options={audit_type_is_there_any_case_error}
                                />
                            </CustomFormControl>
                        </Grid>
                    </Grid>

                    {/* Case Error Items */}
                    <Grid container spacing={4} sx={{ marginTop: "20px" }}>
                        {addNewItem.map((item, index) => (
                            <Grid item xs={12} key={index}>
                                <Box
                                    sx={{
                                        border: "1px solid #ddd",
                                        borderRadius: "8px",
                                        padding: "16px",
                                        backgroundColor: "#fafafa",
                                        marginBottom: "20px",
                                    }}
                                >
                                    <Typography variant="h6" gutterBottom>
                                        Case Error {index + 1}
                                    </Typography>
                                    <Grid container spacing={4}>
                                        <Grid item xs={12} sm={4}>
                                            <CustomInputLabel label="Select Category" />
                                            <CustomFormControl>
                                                <CustomSelect
                                                    value={item.select_category}
                                                    onChange={(e) => handleInputnewchange(index, 'select_category', e.target.value)}
                                                    options={case_select_category}
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12} sm={4}>
                                            <CustomInputLabel label="Error Definition" />
                                            <CustomFormControl>
                                                <CustomSelect
                                                    value={item.errorDefinition || ''}
                                                    onChange={(e) => handleInputnewchange(index, 'errorDefinition', e.target.value)}
                                                    options={case_error_definition}
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12} sm={4}>
                                            <CustomInputLabel label="Final Error Category" />
                                            <CustomFormControl>
                                                <CustomSelect
                                                    value={item.finalErrorCategory || ''}
                                                    onChange={(e) => handleInputnewchange(index, 'finalErrorCategory', e.target.value)}
                                                    options={case_final_error_category}
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12} sm={6}>
                                            <CustomInputLabel label="Remarks" />
                                            <CustomFormControl>
                                                <CustomTextField
                                                    value={item.remarks}
                                                    onChange={(e) => handleInputnewchange(index, "remarks", e.target.value)}
                                                    multiline
                                                    rows={4}
                                                    placeholder="Enter remarks here"
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12} sm={6}>
                                            <CustomInputLabel label="Remarks for Operations" />
                                            <CustomFormControl>
                                                <CustomTextField
                                                    value={item.remarks_for_operations}
                                                    onChange={(e) => handleInputnewchange(index, "remarks_for_operations", e.target.value)}
                                                    multiline
                                                    rows={4}
                                                    placeholder="Enter remarks for operations here"
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12}>
                                            <CustomFormControl>
                                                <input
                                                    type="file"
                                                    multiple
                                                    onChange={(e) => handleFileChange(index, e.target.files)}
                                                    style={{ width: "100%", border: "1px solid #ddd", padding: "8px", borderRadius: "4px" }}
                                                />
                                            </CustomFormControl>
                                        </Grid>
                                        <Grid item xs={12}>
                                            <Grid container spacing={2} alignItems="center">
                                                <Grid item>
                                                    <CustomButton
                                                        onClick={() => handleRemoveNewItem(index)}
                                                        variant="outlined"
                                                        color="error"
                                                        sx={{ marginBottom: "20px", marginRight: "10px" }}
                                                    >
                                                        Remove Item
                                                    </CustomButton>
                                                </Grid>
                                                <Grid item>
                                                    <CustomButton
                                                        onClick={handleAddNewItem}
                                                        sx={{ marginBottom: "20px" }}
                                                    >
                                                        Add Item
                                                    </CustomButton>
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                </Box>
                            </Grid>
                        ))}
                    </Grid>

                    {/* Additional Components */}
                    <Cwe onComponentsUpdatecwe={handleComponentsUpdate} />
                    <Cwithoute onComponentsUpdate={handleComponentsUpdateforcwe} />

                    {/* Submit Button */}
                    <Grid container spacing={2} justifyContent="flex-end" sx={{ marginTop: "20px" }}>
                        <Grid item>
                            <CustomButton type="submit" variant="contained">
                                Submit
                            </CustomButton>
                        </Grid>
                    </Grid>
                </form>
            </Box>
        </Paper>
    </Box>
</>



        
    );
};

export default Landing;
