import React, { useState } from 'react';
import { Select, MenuItem, InputLabel, FormControl } from '@mui/material';

const CustomSelect = ({ value, onChange, options, placeholder }) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleToggle = (event) => {
        event.stopPropagation();
        setIsOpen(!isOpen);
    };

    const handleOptionSelect = (event, selectedValue) => {
        onChange(selectedValue);
        setIsOpen(false);
    };

    return (
        <div
            className={`custom-select ${isOpen ? 'opened' : ''}`}
            onClick={handleToggle}
            style={{
                position: 'relative',
                display: 'inline-block',
                userSelect: 'none',
            }}
        >
            <span
                className="custom-select-trigger"
                style={{
                    display: 'block',
                    width: '130px',
                    padding: '0 84px 0 22px',
                    fontSize: '22px',
                    fontWeight: '300',
                    color: '#fff',
                    lineHeight: '60px',
                    background: '#5c9cd8',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    position: 'relative',
                }}
            >
                {value || placeholder}
                <span
                    className="custom-select-trigger-arrow"
                    style={{
                        position: 'absolute',
                        top: '50%',
                        right: '25px',
                        width: '10px',
                        height: '10px',
                        borderBottom: '1px solid #fff',
                        borderRight: '1px solid #fff',
                        transform: 'rotate(45deg) translateY(-50%)',
                        transition: 'all .4s ease-in-out',
                        transformOrigin: '50% 0',
                    }}
                ></span>
            </span>
            <div
                className="custom-options"
                style={{
                    position: 'absolute',
                    top: '100%',
                    left: '0',
                    right: '0',
                    minWidth: '100%',
                    margin: '15px 0',
                    border: '1px solid #b5b5b5',
                    borderRadius: '4px',
                    boxSizing: 'border-box',
                    boxShadow: '0 2px 1px rgba(0,0,0,.07)',
                    background: '#fff',
                    opacity: isOpen ? 1 : 0,
                    visibility: isOpen ? 'visible' : 'hidden',
                    pointerEvents: isOpen ? 'all' : 'none',
                    transform: isOpen ? 'translateY(0)' : 'translateY(-15px)',
                    transition: 'all .4s ease-in-out',
                }}
            >
                {options.map((item, index) => (
                    <span
                        key={index}
                        className={`custom-option ${value === item.value ? 'selection' : ''}`}
                        onClick={(e) => handleOptionSelect(e, item.value)}
                        style={{
                            display: 'block',
                            padding: '0 22px',
                            borderBottom: '1px solid #b5b5b5',
                            fontSize: '18px',
                            fontWeight: '600',
                            color: '#b5b5b5',
                            lineHeight: '47px',
                            cursor: 'pointer',
                            transition: 'all .4s ease-in-out',
                        }}
                    >
                        {item.name}
                    </span>
                ))}
            </div>
        </div>
    );
};

export default CustomSelect;
